var retrieveElementId = function (submodelElement, elementId, elementIds) {

    elementId = elementId + submodelElement.idShort;
    if (submodelElement.modelType.name == "SubmodelElementCollection") {
        elementId = elementId + "_";
        let values = submodelElement.value;
        for (let valueIndex = 0; valueIndex < values.length; valueIndex++) {
            retrieveElementId(values[valueIndex], elementId, elementIds);
        }
    } else {
        elementId = elementId + ":" + submodelElement.value;
        elementIds.push(elementId);
    }
    return true;
}

